memberdef.initializer:
if (mode_string == MODE_SAPDB) {                                      \
    METAPRINTF((a, "\"" b "\"", c));                                      \
  } else if (mode_string == MODE_PGSQL || mode_string == MODE_MYSQL || mode_string == MODE_HYRISE) { \
    METAPRINTF((a, b, c));                                              \
  }

if (mode_string == MODE_SAPDB) {                                      \
    METAPRINTF((a, "\"" b "\""));                                         \
  } else if (mode_string == MODE_PGSQL || mode_string == MODE_MYSQL || mode_string == MODE_HYRISE) { \
    METAPRINTF((a, b));                                                 \
  }
